'From Squeak4.4 of 31 December 2012 [latest update: #12327] on 29 July 2013 at 12:47:29 am'!"Change Set:		SmalllandFontI18NFix-muDate:			29 July 2013Author:			Masashi UmezawaChanged SmallLand theme fonts to KomatunaP for displaying multibyte texts."!!Preferences class methodsFor: 'themes - small-land' stamp: 'mu 7/29/2013 00:31'!smallLandBigFonts	"private - change the fonts to small-land's choices"	"	Preferences smallLandBigFonts.	"	#(		(setButtonFontTo:			#KomatunaP	15)		(setListFontTo:				#KomatunaP		15)		(setMenuFontTo:			#KomatunaP		15)		(setSystemFontTo:			#KomatunaP		15)		(setWindowTitleFontTo:	#KomatunaP		15)		(setCodeFontTo:				#KomatunaP		15)		(setFlapsFontTo:				#KomatunaP					24)		(setEToysFontTo:			#KomatunaP					24)		(setHaloLabelFontTo:		#KomatunaP					24)		(setEToysTitleFontTo:		#KomatunaP					36) 	)		do: [:triplet |			Preferences				perform: triplet first				with: (StrikeFont familyName: triplet second pointSize: triplet third)		].	BalloonMorph setBalloonFontTo: (StrikeFont familyName: #KomatunaP pointSize: 15).! !!Preferences class methodsFor: 'themes - small-land' stamp: 'mu 7/29/2013 00:40'!smallLandFonts	"private - change the fonts to small-land's  	choices"	" 	Preferences smallLandFonts.  	"	Preferences bigDisplay		ifTrue: [^ self smallLandBigFonts].	Preferences tinyDisplay		ifTrue: [^ self smallLandTinyFonts].	self smallLandSmallFonts.		Locale localeChanged.! !!Preferences class methodsFor: 'themes - small-land' stamp: 'mu 7/29/2013 00:33'!smallLandSmallFonts	"private - change the fonts to small-land's choices"	"	Preferences smallLandFonts.	"	#(		(setButtonFontTo:			#KomatunaP	12)		(setListFontTo:				#KomatunaP		12)		(setMenuFontTo:			#KomatunaP		12)		(setSystemFontTo:			#KomatunaP		12)		(setWindowTitleFontTo:	#KomatunaP		15)		(setCodeFontTo:				#KomatunaP		12)		(setFlapsFontTo:				#KomatunaP					15)		(setEToysFontTo:			#KomatunaP					15)		(setHaloLabelFontTo:		#KomatunaP					15)		(setEToysTitleFontTo:		#KomatunaP					24) 	)		do: [:triplet |			Preferences				perform: triplet first				with: (StrikeFont familyName: triplet second pointSize: triplet third)		].	BalloonMorph setBalloonFontTo: (StrikeFont familyName: #KomatunaP pointSize: 12).! !!Preferences class methodsFor: 'themes - small-land' stamp: 'mu 7/29/2013 00:32'!smallLandTinyFonts	"private - change the fonts to small-land's choices"	"	Preferences smallLandTinyFonts.	"	#(		(setButtonFontTo:			#KomatunaP	9)		(setListFontTo:				#KomatunaP		9)		(setMenuFontTo:			#KomatunaP		9)		(setSystemFontTo:			#KomatunaP		9)		(setWindowTitleFontTo:	#KomatunaP		10)		(setCodeFontTo:				#KomatunaP		9)		(setFlapsFontTo:				#KomatunaP		11)		(setEToysFontTo:			#KomatunaP		11)		(setHaloLabelFontTo:		#KomatunaP		11)		(setEToysTitleFontTo:		#KomatunaP		11) 	)		do: [:triplet |			Preferences				perform: triplet first				with: (StrikeFont familyName: triplet second pointSize: triplet third)		].	BalloonMorph setBalloonFontTo: (StrikeFont familyName: #KomatunaP pointSize: 9).! !