'From Squeak4.1 of 17 April 2010 [latest update: #9957] on 24 April 2010 at 11:52:39 pm'!!TestRunner methodsFor: 'accessing-categories' stamp: 'mu 1/27/2008 18:31'!categoryMenu: aMenu	^ aMenu		title: 'Categories' translated;		add: 'Select all' translated action: #selectAllCategories;		add: 'Select inversion' translated action: #selectInverseCategories;		add: 'Select none' translated action: #selectNoCategories;		addLine;		add: 'Filter...' translated action: #filterCategories;		addLine;		add: 'Refresh' translated action: #updateCategories;		yourself.! !!TestRunner methodsFor: 'accessing-classes' stamp: 'mu 4/24/2010 23:52'!classMenu: aMenu	^ aMenu		title: 'Classes' translated;		add: 'Browse' translated action: #browseClass;		addLine;		add: 'Select all' translated action: #selectAllClasses;		add: 'Select subclasses' translated action: #selectSubclasses;		add: 'Select inversion' translated action: #selectInverseClasses;		add: 'Select none' translated action: #selectNoClasses;		addLine;		add: 'Filter...' translated action: #filterClasses;		addLine;		add: 'Refresh' translated action: #updateClasses;		yourself.! !!TestRunner methodsFor: 'building' stamp: 'mu 1/27/2008 18:26'!buildButtonsWith: aBuilder	^ aBuilder pluggablePanelSpec new		model: self;		layout: #horizontal;		children: (self buttons collect: [ :each |			aBuilder pluggableButtonSpec new				model: self; 				label: each first translated;				action: each second;				enabled: each third;				yourself ]);		yourself.! !